import React, { Component } from 'react';
import logo from './logo.svg';
import axios from 'axios';
import './App.css';
import sai from './sai';
import { HashRouter as Router, Route, Link } from 'react-router-dom';
import { Card, CardImg, CardText, CardBody, CardTitle, CardSubtitle, Button,Row, Col,Container } from 'reactstrap';
import 'bootstrap/dist/css/bootstrap.css';
class Form extends Component {

  constructor(props){
    super(props);

    this.state = {
      stories:[]
    }

    axios.post('http://10.90.90.102:3002/').then(res=>{

      if(res.data.status == "success"){

        this.setState({stories:res.data.story});
       console.log(this.state.stories);

      }


    })

  }
  render() {
    return (
      <Container>

      <Row >
        <Col sm="4">
          <Card>
            <CardImg top width="100%" src="./index.jpeg" />
            <CardBody>
              <CardTitle><h1>{this.state.stories.title}</h1></CardTitle>

              <CardText>{this.state.stories.story}</CardText>
              <Button>read more</Button>
            </CardBody>
          </Card>
        </Col>
        <Col sm="4">
          <Card>
            <CardImg top width="100%" alt="image" />
            <CardBody>
              <CardTitle><h1>{this.state.stories.title}</h1></CardTitle>

              <CardText>{this.state.stories.story}</CardText>
              <Button>read more</Button>
            </CardBody>
          </Card>

        </Col>
        <Col sm="4">
          <Card>
            <CardImg top width="100%" alt="image" />
            <CardBody>
              <CardTitle><h1>{this.state.stories.title}</h1></CardTitle>

              <CardText>{this.state.stories.story}</CardText>
              <Button>read more</Button>
            </CardBody>
          </Card>
        </Col>
      </Row>



       <Route path="/link" component={sai}></Route>

      </Container>
    );
  }
}

export default Form;
