import React, { Component } from 'react';
import logo from './logo.svg';
import axios from 'axios';
import './App.css';
import sai from './sai';
import { HashRouter as Router, Route, Link } from 'react-router-dom';
import { Card, CardImg, CardText, CardBody, CardTitle, CardSubtitle, Button } from 'reactstrap';

class Form extends Component {

  constructor(props){
    super(props);

    this.state = {
      stories:[]
    }

    axios.post('http://10.90.90.102:3002/').then(res=>{

      if(res.data.status == "success"){

        this.setState({stories:res.data.story});
       console.log(this.state.stories);

      }


    })

  }
  render() {
    return (
      <div className="App">

        <Card>
        <CardImg top width="100%" alt="Card image cap" />
        <CardBody>
          <CardTitle>{this.state.stories.title}</CardTitle>

          <CardText>{this.state.stories.description}</CardText>
          <Button>read more</Button>
        </CardBody>
      </Card>

       <Route path="/link" component={sai}></Route>

      </div>
    );
  }
}

export default Form;
