import React, { Component } from 'react';
//import logo from './logo.svg';
import axios from 'axios';
import './index.css';

import Story from './story';
import { HashRouter , Route, NavLink } from 'react-router-dom';
import { Card, CardImg, CardText, CardBody, CardTitle, CardSubtitle,
       Button,Row, Col,Container } from 'reactstrap';
import 'bootstrap/dist/css/bootstrap.css';

class Culture extends Component {

  constructor(props){
    super(props);

    this.state = {
      stories:[],
      resentPost:''

    }

    axios.post('http://10.90.90.102:3002/').then(res=>{

      if(res.data.status === "success"){

        this.setState({stories:res.data.story});
       console.log(this.state.stories);

      }
      })

  }
  render() {


    var rowStyle={
      padding:"10px"
    }

    var hrStyle={
      backgroundColor:'black'
    }

    const stories = this.state.stories.map((d) => 

            <Row style={rowStyle}>
              <Col sm="4">
                <img src="images.jpeg" height="100px" width="100px" alt="poster"/>
              </Col>
              <Col sm="8">{d.description}<NavLink to="/Story">Read-more</NavLink></Col>
            </Row>
      );

    this.state.stories.map((item, index)=>{


        if(this.state.stories.length === index+1){
          this.setState({resentPost:item});
          console.log(this.state.resentPost);

        }



      })






    return (
      <HashRouter>
    <Container>
        <Row>

          <Col sm="4">
            <Card>
              <CardImg top width="100%" src="images.jpeg" alt="Card image cap" />
              <CardBody>
                <CardTitle>{this.state.resentPost.title}</CardTitle>
                <CardText>{this.state.resentPost.description}</CardText>
                <Button>read more</Button>
              </CardBody>
            </Card>
          </Col>

          <Col sm="4">

            {stories}

          </Col>
          <Col sm="4">
            <Card>
              <CardImg top width="100%" src="images.jpeg" alt="Card image cap" />
              <CardBody>
                <CardTitle>Card title</CardTitle>
                <CardSubtitle>Card subtitle</CardSubtitle>
                <CardText>Some quick example text to build on the card title and make up the bulk of the card's content.</CardText>
                <Button>Button</Button>
              </CardBody>
            </Card>
          </Col>
        </Row>



        <hr style={hrStyle}/>

         <div>
        <Route path="/Story" component={Story}/>
      </div>   


      </Container> 
</HashRouter>

    );
  }
}

export default Culture;
