import React, { Component } from 'react';
//import logo from './logo.svg';
import axios from 'axios';
import './index.css';


import { HashRouter , NavLink } from 'react-router-dom';
import { Card, CardImg, CardText, CardBody, CardTitle, CardSubtitle,
       Button,Row, Col,Container } from 'reactstrap';
import 'bootstrap/dist/css/bootstrap.css';

class Culture extends Component {

  constructor(props){
    super(props);

    this.state = {
      stories:[],
      resentPost:{},
      storiesThree:[]

    }

    axios.post('http://10.90.90.102:3002/').then(res=>{

      if(res.data.status === "success"){

        this.setState({stories:res.data.story});


        for (var i = 0; i < res.data.story.length; i++) {
          if(res.data.story.length === i + 1){
            this.setState({ resentPost: res.data.story[i] })


          }
        }


        for (var i = 0; i < res.data.story.length; i++) {
          if (res.data.story[i] < 2) {
            this.setState({ storiesThree: res.data.story[i] })
            console.log('sssssssssss', this.state.storiesThree);
            break;
          }
        }


      }
      })


   }

  render() {


    var rowStyle={
      padding:"10px"
    }

    var hrStyle={
      backgroundColor:'black'
    }

    const stories = this.state.stories.map((d) => 

            <Row style={rowStyle}>
              <Col sm="4">
                <img src="images.jpeg" height="100px" width="100px" alt="poster"/>
              </Col>
              <Col sm="8" key={d}>{d.description}<NavLink to="/Story">Read-more</NavLink></Col>
            </Row>

      );



    return (
      <HashRouter>
    <Container>
        <Row>

          <Col sm="4">
            <Card>
              <CardImg top width="100%" src="images.jpeg" alt="Card image cap" />
              <CardBody>
                <CardTitle>{this.state.resentPost.title}</CardTitle>
                <CardText>{this.state.resentPost.description}</CardText>
                <NavLink to="/Story">Read-more</NavLink>
              </CardBody>
            </Card>
          </Col>

          <Col sm="4">

            {stories}

          </Col>
          <Col sm="4">
            <Card>
              <CardImg top width="100%" src="images.jpeg" alt="Card image cap" />
              <CardBody>
                <CardTitle>Card title</CardTitle>
                <CardSubtitle>Card subtitle</CardSubtitle>
                <CardText>Some quick example text to build on the card title and make up the bulk of the card's content.</CardText>
                <NavLink to="/Story">Read-more</NavLink>
              </CardBody>
            </Card>
          </Col>
        </Row>



        <hr style={hrStyle}/>
      </Container> 
</HashRouter>

    );
  }
}

export default Culture;
