import React, { Component } from 'react';
import axios from 'axios';
// import Login from './login';
import { HashRouter as Router, Route, Link } from 'react-router-dom';
// import Notification from './notifications'; 
// import {NotificationContainer, NotificationManager} from 'react-notifications';
// import logo from './logo.svg';
// import './App.css';
//import dataValidate from './components/dataValidate.js'

class Form extends Component{

    constructor(props){
        super(props);

        this.state ={
            name:'',
            rollNumber:'',
            phoneNumber:'',
            sport:'',
            registrationNumber:'',
            registrationStatus:'',
            registrationNumberShow:''

        }

        this.handleChange = this.handleChange.bind(this);
        this.handleSubmit = this.handleSubmit.bind(this);

    }

        handleChange(event) {


            if (event.target.name === 'nameInput') {
                this.setState({ 'name': event.target.value });
            }
            if (event.target.name === 'rollNumberInput') {
                this.setState({ 'rollNumber': event.target.value });
            }
            if (event.target.name === 'phoneNumberInput') {
                this.setState({ 'phoneNumber': event.target.value });
            }
            if (event.target.name === 'sportInput') {
                this.setState({ 'sport': event.target.value });
            }

           this.setState({'registrationNumber':Math.floor(Math.random()*1000000)});

        }

        handleSubmit(event) {

            event.preventDefault();

            var nameTest = /([\w]){3,}/;
            var rollNumberTest = /([\d]{1,6})/;
            var phoneNumberTest = /([\d]{10})/;
            var sportTest = /([\w]{3,10})/;



            if( nameTest.test(this.state.name) && rollNumberTest.test(this.state.rollNumber) && phoneNumberTest.test(this.state.phoneNumber) && sportTest.test(this.state.sport)){


                var data ={
                    name:this.state.name,
                    rollNumber:this.state.rollNumber,
                    phoneNumber:this.state.phoneNumber,
                    sport:this.state.sport,
                    registrationNumber:this.state.registrationNumber
                }






        axios.post('http://10.90.90.102:3002/students_data', data ).then(res => {

        console.log(res.data);

        console.log(res.data.reguser);


        if(res.data.status === 'failed'){
            alert("already registered with this rollNumber");
        }else{
            this.setState({registrationStatus:'success'});
            this.setState({registrationNumberShow:res.data.reguser});
        }

        })//axios post closed

        }

        else{

           alert("Credentials not matched and all fields are mandatory");   
        }

  }

    render(){

        var tableStyle = {
                padding:'10px',
                margin:'5px',
                color:'black',

            };

            var btnStyle ={
                'backgroundColor':'green',
                padding:'10px',
                color:'white',
                outline:'none',
                'borderRadius':'10px',
                border:'none'
            };



    return(


        <div align="center"><h1>Welcome to Sports-Competition</h1>

            <form onSubmit={this.handleSubmit}>

                <table style={tableStyle}>

                    <tbody style={tableStyle}>

                        <tr>
                            <td>Name of the Student:</td>
                            <td><input type = "text" placeholder = "Name here" name= "nameInput" onChange={this.handleChange}/></td>
                        </tr>

                        <tr>
                            <td>Roll number:</td>
                            <td><input type = "text" placeholder = "Roll-number here" name= "rollNumberInput" onChange={this.handleChange}/></td>
                        </tr>

                        <tr>
                            <td>Phone number:</td>
                            <td><input type = "text" placeholder = "Phone number here" name="phoneNumberInput" onChange={this.handleChange}/></td>
                        </tr>

                        <tr>
                            <td>Interest of Sports</td>
                            <td><input type = "text" placeholder = "sports name here" name="sportInput" onChange={this.handleChange}/></td>
                        </tr>

                        <tr>
                           <td colSpan="2" align="center">
                                <div style = {tableStyle}><input type="submit" style={btnStyle} value ="participate"/></div></td>
                        </tr>
                    </tbody>
                </table>
            </form>

            {
                this.state.registrationStatus ?
                <div>
                <p>successfully registered </p>
                <p>Your registration number is : {this.state.registrationNumberShow.registrationNumber}</p>
                </div>:''
            }

            <div><h3>If you alredy registered: <Router><Link to = "/login">Showdata</Link></Router></h3></div>






                    <ol type="1" className="rules">
                     <h3>Rules and Regulations</h3>
                    <li>Only one participation for one student</li>
                    <li>Breakfast, lunch will be provide by us </li>
                    <li>Umpire decision is final</li>
                    <li>Venue, opposition teams details will be provide before two days of Sports-Competition</li>
                    <li><h4>***School ID proofs are Mandatory at the time of competitions***</h4></li>
                    </ol>


            </div>


        )
    }

}
export default Form;
